	 	report.config()
              .documentTitle("API Automation Report")
              .reportName("SMOKE TEST");
		logger=report.startTest("Test case mohit");
		logger.log(LogStatus.INFO, "Checking for Response1..");
		logger.log(LogStatus.PASS, "Status for test1");
		report.endTest(logger);
		report.flush();
	 } 
