//Define Groovy Utils and holder for validating the XML reponse content
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def response = context.expand('${initiateTransaction#Response}')
def holder = groovyUtils.getXmlHolder(response)
//def holder = groovyUtils.getXmlHolder(messageExchange.responseContent)

//Define the NameSpace
//holder.namespaces["ns"] = "http://public-consul-qa-753928567.us-west-2.elb.amazonaws.com"

//Get the Value of the Status and assign to a variable
def status1 = holder.getNodeValue("//ns:status")

//print the value of the Status in the Output pane
log.info "Status for request is " + status1

//Comparing the value to print 'Pass' or 'Fail'
if(status1=="200")
{ log.info "Pass" }
else
{ log.info "fail"}

