con:config.script:
import utilities.*;

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def myTestCase = context.testCase
def projectDir = groovyUtils.projectPath
myvar = context.expand('${#Project#WorkbookNamePath}')
def path = projectDir +'\\'+ myvar
def Sname = context.expand('${#Project#EventsSheetNo}')
log.info path
log.info Sname
log.info "Reading Input test files..."
//def path = "D:\\Master\\DataSheet\\Trigsted.xlsx"
//def Sname = "Question"
Object[][] ObjAr = ExcelUtils.getTableArray(path,Sname)
//============================================================= 
log.info ObjAr[1][2]

def size = ObjAr.size().toInteger()
log.info size
propTestStep = myTestCase.getTestStepByName("Properties")
propTestStep.setPropertyValue("Total", size.toString())

counter = propTestStep.getPropertyValue("Count").toString()
counter = counter.toInteger()
next = (counter > size-2? 0: counter+1)

propTestStep.setPropertyValue("Count", next.toString())

tempValue = ObjAr[counter]
propTestStep.setPropertyValue("EventType", tempValue[0])
propTestStep.setPropertyValue("Desc", tempValue[1])
propTestStep.setPropertyValue("Publishers", tempValue[2])
propTestStep.setPropertyValue("SubscriberID", tempValue[3])
propTestStep.setPropertyValue("SubscriberDestinationType", tempValue[4])
propTestStep.setPropertyValue("SubscriberDestinationRetryCount", tempValue[5])
propTestStep.setPropertyValue("SubscriberDestinationRetryInterval", tempValue[6])
propTestStep.setPropertyValue("SubscriberDestinationCommand", tempValue[7])
propTestStep.setPropertyValue("CourseName", tempValue[8])
propTestStep.setPropertyValue("ProductID", tempValue[9])
propTestStep.setPropertyValue("Heading", tempValue[10])
propTestStep.setPropertyValue("subscriberDesc", tempValue[11])
propTestStep.setPropertyValue("EventId", tempValue[12])

next++


log.info "Reading line : ${(counter+1)} / $size"
propTestStep.setPropertyValue("Next", next.toString())
log.info "Value '$tempValue' -- updated in $propTestStep.name"


//Renaming Test Request on the go
def reqTestStep = testRunner.testCase.getTestStepAt(2);
def reqTestStepName = "ISC - Create Course Creation Event";
tempTestStepName = reqTestStepName + " - " + tempValue[0];
testRunner.testCase.getTestStepAt(2).setName(tempTestStepName);

if (counter == size-1)
{
propTestStep.setPropertyValue("StopLoop", "T")
log.info "Setting the stoploop property now..."
}
else if (counter==0)
{
def runner = new com.eviware.soapui.impl.wsdl.testcase.WsdlTestCaseRunner(testRunner.testCase, null)
propTestStep.setPropertyValue("StopLoop", "F")
}
else
{
propTestStep.setPropertyValue("StopLoop", "F")
}

def myTestCase = context.testCase

def runner
propTestStep = myTestCase.getTestStepByName("Properties") // get the Property TestStep
endLoop = propTestStep.getPropertyValue("StopLoop").toString()

if (endLoop.toString() == "T" || endLoop.toString()=="True" || endLoop.toString()=="true")
{
 log.info ("Exit Groovy Data Source Looper")
 assert true
}
else
{
 testRunner.gotoStepByName("Read")
}

import utilities.*;

log.info "Configuring properties..."
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def projectDir = groovyUtils.projectPath
def myTestCase = context.testCase
log.info projectDir

Properties properties = new Properties()
File propertiesFile = new File(projectDir + '\\config.properties')
propertiesFile.withInputStream {def project = context.testCase.testSuite.project
    properties.load(it)
}

def project = context.testCase.testSuite.project
project.setPropertyValue("TemplateSheetNo",properties.TemplateSheetNo)

log.info "Properties added successfully..."
//======================================================
myvar = context.expand('${#Project#WorkbookNamePath}')
def path = projectDir +'\\'+ myvar
def Exec = context.expand('${#Project#Execution}')
log.info path
log.info "Reading Input test files..."
Object[][] ObjAr = ExcelUtils.getTableArray(path,Exec)
try{
def testSuite = context.testCase.testSuite;
def totalTestCases = testSuite.getTestCases().size();
log.info "Total testCases are " + totalTestCases
def size = ObjAr.size().toInteger()

	for (x in (0..ObjAr.size()-1)){

		if(ObjAr[x][1].toString()=="No"){	
		testSuite.getTestCaseByName(ObjAr[x][0].toString()).setDisabled(true)
		log.info "Skipping TestCase : "+(testSuite.getTestCaseAt(x).name)
		}
		else if (testSuite.getTestCaseByName(ObjAr[x][0].toString()).isDisabled()){
			testSuite.getTestCaseByName(ObjAr[x][0].toString()).setDisabled(false)
			log.info "Enabling Test Cases : " +(testSuite.getTestCaseAt(x).name)
		}
	}	
}
catch(Exception e){
	log.info e
}

